/* eslint-disable no-console */

if (process.env.MINIDEV_SKIP_DOWNLOAD_ASSETS) {
  console.log("skip download assets because `SKIP_DOWNLOAD_ASSETS` env variable is set.");
  process.exit(0);
}

async function postinstall() {
  await require('../lib').minidev.downloadAllAssets();
}

try {
  postinstall().catch(e => {
    console.warn('minidev post install failed, skip');
  });
} catch(e) {
  console.warn('minidev post install failed, skip');
}